from typing import List

class Solution:
    def getSumAbsoluteDifferences(self, nums: List[int]) -> List[int]:
        prefix, suffix, res = 0, sum(nums), []
        for i in range(len(nums)):
            res.append((i * nums[i] - prefix) + (suffix - (len(nums) - i) * nums[i]))
            prefix += nums[i]
            suffix -= nums[i]
        return res

    def getSumAbsoluteDifferences_BruteForce(self, nums: List[int]) -> List[int]:
        result = []
        for num in nums:
            _tempResult = 0
            for j in range(len(nums) - 1, -1, -1):
                _tempResult += abs(nums[j] - num)
            result.append(_tempResult)
        return result


